package main

import (
	"fmt"
)

func main() {
	N := 14 // just change to 4 for part 1
	p2 := 0
	input := ""

	findFirstUniqueN:
	for i := 0; i < len(input); i+=1 {
		hash := make(map[string]int)
		for j, v := range input[i:i+N] {
			hash[string(v)] = j
		}
		if len(hash) == N {
			p2 = i + N
			break findFirstUniqueN
		}
	}

	fmt.Println(fmt.Sprintf("%s%d", "P2: ", p2))

}
